import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";

function EditSalary() {
  const [salary, setSalary] = useState("");
  const [position, setPosition] = useState("");
  const [depId, setDepId] = useState("");
  const [departments, setDepartments] = useState([]);
  const navigate = useNavigate();
  const { id } = useParams();

  useEffect(() => {
    getSalaryByID();
  }, [id]);

  const getSalaryByID = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/salary/${id}`);
      console.log("Full API Response:", response.data);

      setDepId(response.data.dep_id);
      setPosition(response.data.position);
      setSalary(response.data.salary);
    } catch (error) {
      console.error("Error fetching salary:", error);
    }
  };

  useEffect(() => {
    const fetchDepartments = async () => {
      try {
        const response = await axios.get("http://localhost:5000/department");
        setDepartments(response.data);
      } catch (error) {
        console.error("Error fetching departments:", error);
      }
    };
    fetchDepartments();
  }, []);

  const saveSalary = async (e) => {
    e.preventDefault();
    try {
      await axios.patch(`http://localhost:5000/salary/${id}`, {
        dep_id: depId,
        position,
        salary: parseFloat(salary) || 0,
      });
      navigate("/");
    } catch (error) {
      console.error("Error updating salary:", error);
    }
  };

  return (
    <div className="columns mt-5 is-centered">
      <div className="column is-half">
        <form onSubmit={saveSalary}>
          {/* Department */}
          <div className="field">
            <label className="label">Department</label>
            <div className="control">
              <div className="select is-fullwidth">
                <select
                  value={depId}
                  onChange={(e) => setDepId(e.target.value)}
                  required
                >
                  <option value="">Select a department</option>
                  {departments.map((dep) => (
                    <option key={dep.id} value={dep.id}>
                      {dep.Department_Name}
                    </option>
                  ))}
                </select>
              </div>
            </div>
          </div>

          {/* Position */}
          <div className="field">
            <label className="label">Position</label>
            <div className="control">
              <input
                type="text"
                className="input"
                value={position}
                onChange={(e) => setPosition(e.target.value)}
                placeholder="Position"
                required
              />
            </div>
          </div>

          {/* Salary */}
          <div className="field">
            <label className="label">Salary</label>
            <div className="control">
              <input
                type="number"
                className="input"
                value={salary}
                onChange={(e) => setSalary(e.target.value)}
                placeholder="Salary"
                required
              />
            </div>
          </div>

          {/* Submit Button */}
          <div className="field">
            <button type="submit" className="button is-success">
              Save
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default EditSalary;
