import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

function EditSalary() {
  const [Salary, setSalary] = useState("");
  const [Position, setPosition] = useState("");
  const [dep_id, setDept] = useState("");
  const [departments, setDepartments] = useState([]);
  //   const navigate = useNavigate();
  const { id } = useParams();

  useEffect(() => {
    getSalaryByID();
  }, []);

  const getSalaryByID = async () => {
    const response = await axios.get(`http://localhost:5000/salary/${id}`);
    setDept(response.data.dep_id);
    setPosition(response.data.position);
    setSalary(response.data.salary);
  };

  useEffect(() => {
    const fetchDepartments = async () => {
      try {
        const response = await axios.get("http://localhost:5000/department");
        setDepartments(response.data);
      } catch (error) {
        console.error("Error fetching departments:", error);
      }
    };
    fetchDepartments();
  }, []);

  return (
    <div className="columns mt-5 is-centered">
      <div className="column is-half">
        <form onSubmit={saveSalary}>
          {/* Department */}
          <div className="field">
            <label className="label">Department</label>
            <div className="control">
              <div className="select is-fullwidth">
                <select
                  value={dep_id}
                  onChange={(e) => setDept(e.target.value)}
                  required
                >
                  <option value="">Select a department</option>
                  {departments.map((dep) => (
                    <option key={dep.id} value={dep.id}>
                      {dep.Department_Name}
                    </option>
                  ))}
                </select>
              </div>
            </div>
          </div>

          {/* Full Name */}
          <div className="field">
            <label className="label">Position</label>
            <div className="control">
              <input
                type="text"
                className="input"
                value={Position}
                onChange={(e) => setPosition(e.target.value)}
                placeholder="Position"
                required
              />
            </div>
          </div>
          {/* Salary */}
          <div className="field">
            <label className="label">Salary</label>
            <div className="control">
              <input
                type="number"
                className="input"
                value={Salary}
                onChange={(e) => setSalary(e.target.value)}
                placeholder="Salary"
                required
              />
            </div>
          </div>
          {/* Submit Button */}
          <div className="field">
            <button type="submit" className="button is-success">
              Save
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default EditSalary;
