import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const AddUser = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [gender, setGender] = useState("Male");
  const navigate = useNavigate();

  // Department & Position State
  const [departments, setDepartments] = useState([]);
  const [dep_id, setDepId] = useState(""); // Department ID
  const [positions, setPositions] = useState([]); // Positions for selected department
  const [position_id, setPositionId] = useState(""); // Selected position
  const [salary, setSalary] = useState(""); // Salary based on position

  // Fetch Departments on Mount
  useEffect(() => {
    const fetchDepartments = async () => {
      try {
        const response = await axios.get("http://localhost:5000/users/dept");
        setDepartments(response.data);
      } catch (error) {
        console.error("Error fetching departments:", error);
      }
    };

    fetchDepartments();
  }, []);

  // // Fetch Positions when Department Changes
  // useEffect(() => {
  //   if (!dep_id) return; // Do nothing if no department selected

  //   const fetchPositions = async () => {
  //     try {
  //       const response = await axios.get(
  //         `http://localhost:5000/users/salary/${dep_id}`
  //       );
  //       setPositions(response.data.positions); // Expecting an array of positions
  //       setPositionId(""); // Reset position when department changes
  //       setSalary(""); // Reset salary when department changes
  //     } catch (error) {
  //       console.error("Error fetching positions:", error);
  //     }
  //   };

  //   fetchPositions();
  // }, [dep_id]);

  // Fetch Salary when Position Changes
  useEffect(() => {
    if (!position_id) return; // Do nothing if no position selected

    const fetchSalary = async () => {
      try {
        const response = await axios.get(
          `http://localhost:5000/users/position/${position_id}`
        );
        setSalary(response.data.salary);
      } catch (error) {
        console.error("Error fetching salary:", error);
      }
    };

    fetchSalary();
  }, [position_id]);

  // Save User
  const saveUser = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/users", {
        name,
        email,
        gender,
        dep_id,
        position_id,
        salary, // Save salary with user
      });
      navigate("/");
    } catch (error) {
      console.error("Error saving user:", error);
    }
  };

  return (
    <div className="columns mt-5 is-centered">
      <div className="column is-half">
        <form onSubmit={saveUser}>
          {/* Full Name */}
          <div className="field">
            <label className="label">Full Name</label>
            <div className="control">
              <input
                type="text"
                className="input"
                value={name}
                onChange={(e) => setName(e.target.value)}
                placeholder="Full Name"
              />
            </div>
          </div>

          {/* Email */}
          <div className="field">
            <label className="label">Email</label>
            <div className="control">
              <input
                type="email"
                className="input"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="Email"
              />
            </div>
          </div>

          {/* Department */}
          <div className="field">
            <label className="label">Department</label>
            <div className="control">
              <div className="select is-fullwidth">
                <select
                  value={dep_id}
                  onChange={(e) => setDepId(e.target.value)}
                >
                  <option value="">Select a department</option>
                  {departments.map((dep) => (
                    <option key={dep.id} value={dep.id}>
                      {dep.Department_Name}
                    </option>
                  ))}
                </select>
              </div>
            </div>
          </div>

          {/* Position */}
          <div className="field">
            <label className="label">Position</label>
            <div className="control">
              <div className="select is-fullwidth">
                <select
                  value={position_id}
                  onChange={(e) => setPositionId(e.target.value)}
                  disabled={!positions.length}
                >
                  <option value="">Select a position</option>
                  {positions.map((pos) => (
                    <option key={pos.id} value={pos.id}>
                      {pos.position}
                    </option>
                  ))}
                </select>
              </div>
            </div>
          </div>

          {/* Salary (Read-Only) */}
          <div className="field">
            <label className="label">Salary</label>
            <div className="control">
              <input
                type="text"
                className="input"
                value={salary}
                placeholder="Salary"
                readOnly
              />
            </div>
          </div>

          {/* Gender */}
          <div className="field">
            <label className="label">Gender</label>
            <div className="control">
              <div className="select is-fullwidth">
                <select
                  value={gender}
                  onChange={(e) => setGender(e.target.value)}
                >
                  <option value="Male">Male</option>
                  <option value="Female">Female</option>
                </select>
              </div>
            </div>
          </div>

          {/* Submit Button */}
          <div className="field">
            <button type="submit" className="button is-success">
              Save
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AddUser;
