import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import jsPDF from "jspdf";
import "jspdf-autotable";

const UserList = () => {
  const [users, setUser] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [entriesToShow, setEntriesToShow] = useState(10);

  useEffect(() => {
    getUsers();
  }, []);

  const getUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/users");
      setUser(response.data);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  const deleteUser = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/users/${id}`);
      getUsers();
    } catch (error) {
      console.error("Error deleting user:", error);
    }
  };

  const generatePDF = () => {
    if (users.length === 0) {
      alert("No data available to export.");
      return;
    }

    const doc = new jsPDF();
    doc.setFontSize(16);
    doc.text("Employee List", 14, 10);

    // Define table headers and rows
    const columns = [
      "ID",
      "Full Name",
      "Email",
      "Gender",
      "Job Title",
      "Position",
      "Salary",
      "Annual Salary",
    ];
    const rows = users.map((user) => [
      user.id,
      user.full_name,
      user.email,
      user.gender,
      user.Job_Title,
      user.position,
      user.salary
        ? `₱${parseFloat(user.salary).toLocaleString("en-PH", {
            minimumFractionDigits: 2,
          })}`
        : "",
      user.anual_salary
        ? `₱${parseFloat(user.anual_salary).toLocaleString("en-PH", {
            minimumFractionDigits: 2,
          })}`
        : "",
    ]);

    // Ensure `autoTable` is available
    if (typeof doc.autoTable !== "function") {
      console.error(
        "autoTable is not recognized. Check your imports and installation."
      );
      return;
    }

    // Generate table
    doc.autoTable({
      head: [columns],
      body: rows,
      startY: 20,
      theme: "grid",
    });

    doc.save("Employee_List.pdf");
  };

  // Filter users based on search term
  const filteredUsers = users.filter((user) =>
    `${user.full_name} ${user.email} ${user.Job_Title} ${user.position} ${user.id}`
      .toLowerCase()
      .includes(searchTerm.toLowerCase())
  );

  // Limit displayed entries based on selection
  const displayedUsers = filteredUsers.slice(0, entriesToShow);

  return (
    <div className="container mt-5">
      {/* Search Bar and Action Buttons */}
      <div className="is-flex is-justify-content-space-between mb-3">
        <input
          type="text"
          className="input"
          style={{ maxWidth: "300px" }}
          placeholder="Search by Employee ID, Name, Email, Job Title, or Position..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <div>
          <button className="button is-info mr-3" onClick={generatePDF}>
            Export to PDF
          </button>
          <Link to="AddEmployee" className="button is-success">
            Add Employee
          </Link>
        </div>
      </div>

      {/* Employee Table */}
      <table className="table is-striped is-fullwidth">
        <thead>
          <tr>
            <th>Employee ID</th>
            <th>Full Name</th>
            <th>Email</th>
            <th>Gender</th>
            <th>Job Title</th>
            <th>Position / Level</th>
            <th>Salary</th>
            <th>Annual Salary</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {displayedUsers.length > 0 ? (
            displayedUsers.map((user) => (
              <tr key={user.id}>
                <td>{user.id}</td>
                <td>{user.full_name}</td>
                <td>{user.email}</td>
                <td>{user.gender}</td>
                <td>{user.Job_Title}</td>
                <td>{user.position}</td>
                <td>
                  {user.salary
                    ? `₱${parseFloat(user.salary).toLocaleString("en-PH", {
                        minimumFractionDigits: 2,
                      })}`
                    : ""}
                </td>
                <td>
                  {user.anual_salary
                    ? `₱${parseFloat(user.anual_salary).toLocaleString(
                        "en-PH",
                        {
                          minimumFractionDigits: 2,
                        }
                      )}`
                    : ""}
                </td>
                <td>
                  <Link
                    to={`edit/${user.id}`}
                    className="button is-small is-info mr-2"
                  >
                    Edit
                  </Link>
                  <button
                    onClick={() => deleteUser(user.id)}
                    className="button is-small is-danger"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="9" className="has-text-centered">
                No users found.
              </td>
            </tr>
          )}
        </tbody>
      </table>

      {/* Footer with Entry Filter */}
      <div className="is-flex is-align-items-center is-justify-content-space-between mt-3">
        <div>
          <label className="mr-2">Show entries:</label>
          <div className="select">
            <select
              value={entriesToShow}
              onChange={(e) => setEntriesToShow(Number(e.target.value))}
            >
              <option value="5">5</option>
              <option value="10">10</option>
              <option value="20">20</option>
              <option value={filteredUsers.length}>All</option>
            </select>
          </div>
        </div>
        <p>
          Showing {displayedUsers.length} of {filteredUsers.length} entries
        </p>
      </div>
    </div>
  );
};

export default UserList;
