import React from "react";
import { Page, Text, View, Document, StyleSheet } from "@react-pdf/renderer";

// Styles for PDF
const styles = StyleSheet.create({
  page: { padding: 20 },
  header: {
    fontSize: 18,
    textAlign: "center",
    marginBottom: 10,
    fontWeight: "bold",
  },
  table: {
    display: "table",
    width: "100%",
    borderStyle: "solid",
    borderWidth: 1,
    borderColor: "#000",
    marginBottom: 10,
  },
  tableRow: { flexDirection: "row" },
  tableCell: {
    flex: 1,
    borderStyle: "solid",
    borderWidth: 1,
    padding: 5,
    fontSize: 10,
  },
});

// Salary PDF Document Component
const SalaryPDF = ({ salaries }) => (
  <Document>
    <Page size="A4" style={styles.page}>
      <Text style={styles.header}>Salary Report</Text>
      <View style={styles.table}>
        <View style={styles.tableRow}>
          <Text style={styles.tableCell}>Salary ID</Text>
          <Text style={styles.tableCell}>Job Title</Text>
          <Text style={styles.tableCell}>Position / Level</Text>
          <Text style={styles.tableCell}>Salary</Text>
        </View>
        {salaries.map((user) => (
          <View style={styles.tableRow} key={user.id}>
            <Text style={styles.tableCell}>{user.id}</Text>
            <Text style={styles.tableCell}>{user.Job_Title}</Text>
            <Text style={styles.tableCell}>{user.position}</Text>
            <Text style={styles.tableCell}>
              {user.salary
                ? `₱${parseFloat(user.salary).toLocaleString("en-PH", {
                    minimumFractionDigits: 2,
                  })}`
                : ""}
            </Text>
          </View>
        ))}
      </View>
    </Page>
  </Document>
);

// ✅ Make sure to use **export default**
export default SalaryPDF;
