import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

function DepartList() {
  const [dept, setDept] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [entriesToShow, setEntriesToShow] = useState(10);
  const [isMobile, setIsMobile] = useState(false);

  useEffect(() => {
    getUsers();

    // Detect screen size for responsive design
    const handleResize = () => setIsMobile(window.matchMedia("(max-width: 768px)").matches);
    
    handleResize(); // Run on mount
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const getUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/job");
      setDept(response.data);
    } catch (error) {
      console.error("Error fetching job data:", error);
    }
  };

  const filteredJobs = dept.filter((job) =>
    job.Job_Title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const displayedJobs = entriesToShow === filteredJobs.length ? filteredJobs : filteredJobs.slice(0, entriesToShow);

  return (
    <div className="container mt-5">
      {/* Search & Add Button */}
      <div className="is-flex is-justify-content-space-between is-align-items-center mb-3">
        <input
          type="text"
          className="input"
          style={{ maxWidth: "300px" }}
          placeholder="Search Job Title..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <Link to={`/AddJob`} className="button is-success ml-3">
          Add Job Title
        </Link>
      </div>

      {/* Table View (Desktop) */}
      {!isMobile && (
        <table className="table is-striped is-fullwidth">
          <thead>
            <tr>
              <th>Job ID</th>
              <th>Job Title</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {displayedJobs.map((user) => (
              <tr key={user.id}>
                <td>{user.id}</td>
                <td>{user.Job_Title}</td>
                <td>
                  <Link to={`/editJobTitle/${user.id}`} className="button is-small is-info">
                    Edit
                  </Link>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {/* Card View (Mobile) */}
      {isMobile && (
        <div className="columns is-multiline">
          {displayedJobs.map((user) => (
            <div key={user.id} className="column is-12">
              <div className="card">
                <header className="card-header">
                  <p className="card-header-title">{user.Job_Title}</p>
                </header>
                <div className="card-content">
                  <p className="subtitle is-6">Job ID: {user.id}</p>
                </div>
                <footer className="card-footer">
                  <Link to={`/editJobTitle/${user.id}`} className="card-footer-item button is-small is-info">
                    Edit
                  </Link>
                </footer>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Entries Filter & Info */}
      <div className="is-flex is-align-items-center is-justify-content-space-between mt-3">
        <div className="is-flex is-align-items-center">
          <label className="mr-2">Show entries:</label>
          <div className="select">
            <select
              value={entriesToShow}
              onChange={(e) => setEntriesToShow(e.target.value === "all" ? filteredJobs.length : Number(e.target.value))}
            >
              <option value="5">5</option>
              <option value="10">10</option>
              <option value="20">20</option>
              <option value="all">All</option>
            </select>
          </div>
        </div>
        <p>
          Showing {displayedJobs.length} of {filteredJobs.length} entries
        </p>
      </div>
    </div>
  );
}

export default DepartList;
