import React from "react";
import { Page, Text, View, Document, StyleSheet } from "@react-pdf/renderer";

// Define styles
const styles = StyleSheet.create({
  page: { padding: 20, fontSize: 10 },
  header: {
    fontSize: 18,
    textAlign: "center",
    marginBottom: 15,
    fontWeight: "bold",
    color: "#333",
  },
  table: {
    display: "table",
    width: "100%",
    borderStyle: "solid",
    borderWidth: 1,
    borderColor: "#000",
    marginBottom: 10,
  },
  tableRow: {
    flexDirection: "row",
    backgroundColor: "#f9f9f9",
  },
  tableHeader: {
    flexDirection: "row",
    backgroundColor: "#4CAF50",
    color: "#fff",
    fontWeight: "bold",
  },
  tableCell: {
    flex: 1,
    borderStyle: "solid",
    borderWidth: 1,
    padding: 8,
    fontSize: 10,
    textAlign: "center",
  },
  boldText: { fontWeight: "bold" },
});

// User PDF Document Component
const EmployeePDF = ({ users }) => (
  <Document>
    <Page size="A4" style={styles.page}>
      <Text style={styles.header}>Employee Report</Text>
      <View style={styles.table}>
        {/* Table Header */}
        <View style={styles.tableHeader}>
          <Text style={[styles.tableCell, styles.boldText]}>ID</Text>
          <Text style={[styles.tableCell, styles.boldText]}>Full Name</Text>
          <Text style={[styles.tableCell, styles.boldText]}>Email</Text>
          <Text style={[styles.tableCell, styles.boldText]}>Job Title</Text>
          <Text style={[styles.tableCell, styles.boldText]}>Salary</Text>
        </View>
        {/* Table Body */}
        {users.map((user, index) => (
          <View
            style={[
              styles.tableRow,
              { backgroundColor: index % 2 === 0 ? "#fff" : "#f1f1f1" },
            ]}
            key={user.id}
          >
            <Text style={styles.tableCell}>{user.id}</Text>
            <Text style={styles.tableCell}>{user.full_name}</Text>
            <Text style={styles.tableCell}>{user.email}</Text>
            <Text style={styles.tableCell}>{user.Job_Title}</Text>
            <Text style={styles.tableCell}>
              {user.salary
                ? `₱${parseFloat(user.salary).toLocaleString("en-PH", {
                    minimumFractionDigits: 2,
                  })}`
                : ""}
            </Text>
          </View>
        ))}
      </View>
    </Page>
  </Document>
);

// Export component
export default EmployeePDF;
