import { StyleSheet, Font } from "@react-pdf/renderer";

const styles = StyleSheet.create({
  page: { padding: 20, fontSize: 10, fontFamily: "Roboto" },
  header: {
    fontSize: 18,
    textAlign: "center",
    marginBottom: 15,
    fontWeight: "bold",
    textTransform: "uppercase",
    color: "#333",
  },
  table: {
    display: "table",
    width: "100%",
    borderStyle: "solid",
    borderWidth: 1,
    borderColor: "#000",
    marginBottom: 10,
  },
  tableHeader: {
    flexDirection: "row",
    backgroundColor: "#4CAF50", // Green header
    color: "#fff",
    fontWeight: "bold",
  },
  tableRow: {
    flexDirection: "row",
    borderBottomWidth: 1,
    borderColor: "#ddd",
  },
  tableCell: {
    flex: 1,
    borderStyle: "solid",
    borderWidth: 1,
    padding: 8,
    fontSize: 10,
    textAlign: "center",
    wordBreak: "break-word",
  },
  emailCell: {
    flex: 2, // Make email column wider
    wordBreak: "break-word",
  },
  salaryCell: {
    flex: 1,
    padding: 8,
    fontSize: 10,
    textAlign: "right", // Right-align salary
    fontWeight: "bold",
  },
});

export default styles;
