import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { pdf } from "@react-pdf/renderer";
import EmployePDF from "../Printing/EmployeePDF";
import ConfirmBox from "../Modal/ConfirmBox";

const UserList = () => {
  const [users, setUser] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [entriesToShow, setEntriesToShow] = useState(10);
  const [deleteData, setDeleteData] = useState(null);
  const [open, setOpen] = useState(false);

  useEffect(() => {
    getUsers();
  }, []);

  const getUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/users");
      setUser(response.data);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  function openDelete(user) {
    setDeleteData(user);
    setOpen(true);
  }

  const deleteUser = async () => {
    if (!deleteData) return;
    try {
      await axios.delete(`http://localhost:5000/users/${deleteData.id}`);
      getUsers();
      setOpen(false);
    } catch (error) {
      console.error("Error deleting user:", error);
    }
  };

  const filteredUsers = users.filter((user) =>
    `${user.full_name} ${user.email} ${user.Job_Title} ${user.position} ${user.id}`
      .toLowerCase()
      .includes(searchTerm.toLowerCase())
  );

  const displayedUsers = filteredUsers.slice(0, entriesToShow);

  const handleViewPDF = async () => {
    const blob = await pdf(<EmployePDF users={users} />).toBlob();
    const url = URL.createObjectURL(blob);
    window.open(url);
  };

  return (
    <div className="container mt-5">
      <div className="is-flex is-justify-content-space-between mb-3">
        <input
          type="text"
          className="input"
          style={{ maxWidth: "300px" }}
          placeholder="Search Employee..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <div>
          <Link to="AddEmployee" className="button is-success mr-2">
            Add Employee
          </Link>
          <button onClick={handleViewPDF} className="button is-info">
            View PDF
          </button>
        </div>
      </div>

      {/* Table View */}
      <table className="table is-striped is-fullwidth is-hidden-mobile">
        <thead>
          <tr>
            <th>Employee ID</th>
            <th>Full Name</th>
            <th>Email</th>
            <th>Job Title</th>
            <th>Position</th>
            <th>Salary</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {displayedUsers.map((user) => (
            <tr key={user.id}>
              <td>{user.id}</td>
              <td>{user.full_name}</td>
              <td>{user.email}</td>
              <td>{user.Job_Title}</td>
              <td>{user.position}</td>
              <td>â‚±{parseFloat(user.salary || 0).toLocaleString()}</td>
              <td>
                <Link
                  to={`edit/${user.id}`}
                  className="button is-small is-info"
                >
                  Edit
                </Link>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Mobile View as Cards */}
      <div className="is-hidden-tablet">
        {displayedUsers.map((user) => (
          <div className="card mb-3" key={user.id}>
            <div className="card-content">
              <p className="title">{user.full_name}</p>
              <p className="subtitle">{user.Job_Title}</p>
              <Link to={`edit/${user.id}`} className="button is-info">
                Edit
              </Link>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default UserList;

