import React from "react";
import { Page, Text, View, Document, StyleSheet, Font } from "@react-pdf/renderer";

// Register a font that supports the Peso sign (₱)
Font.register({
  family: "NotoSans",
  src: "https://raw.githubusercontent.com/googlefonts/noto-fonts/main/hinted/ttf/NotoSans-Regular.ttf",
});

// Styles for PDF
const styles = StyleSheet.create({
  page: { padding: 20, fontSize: 10, fontFamily: "NotoSans" }, // Use NotoSans
  header: {
    fontSize: 18,
    textAlign: "center",
    marginBottom: 15,
    fontWeight: "bold",
    color: "#333",
  },
  table: {
    display: "table",
    width: "100%",
    borderStyle: "solid",
    borderWidth: 1,
    borderColor: "#000",
    marginBottom: 10,
  },
  tableRow: { flexDirection: "row" },
  tableHeader: {
    flexDirection: "row",
    backgroundColor: "#4CAF50",
    color: "#fff",
    fontWeight: "bold",
  },
  tableCell: {
    flex: 1,
    borderStyle: "solid",
    borderWidth: 1,
    padding: 8,
    fontSize: 10,
    textAlign: "center",
    wordBreak: "break-all", // Prevents text overflow
  },
  boldText: { fontWeight: "bold" },
});

// Salary PDF Document Component
const SalaryPDF = ({ salaries }) => (
  <Document>
    <Page size="A4" style={styles.page}>
      <Text style={styles.header}>Salary Report</Text>
      <View style={styles.table}>
        {/* Table Header */}
        <View style={styles.tableHeader}>
          <Text style={[styles.tableCell, styles.boldText]}>Salary ID</Text>
          <Text style={[styles.tableCell, styles.boldText]}>Job Title</Text>
          <Text style={[styles.tableCell, styles.boldText]}>Position / Level</Text>
          <Text style={[styles.tableCell, styles.boldText]}>Salary</Text>
        </View>
        {/* Table Body */}
        {salaries.map((user, index) => (
          <View
            style={[
              styles.tableRow,
              { backgroundColor: index % 2 === 0 ? "#fff" : "#f1f1f1" }, // Alternating row colors
            ]}
            key={user.id}
          >
            <Text style={styles.tableCell}>{user.id || "N/A"}</Text>
            <Text style={styles.tableCell}>{user.Job_Title || "N/A"}</Text>
            <Text style={styles.tableCell}>{user.position || "N/A"}</Text>
            {/* ✅ Fix Peso Sign Display */}
            <Text style={styles.tableCell}>
              {user.salary
                ? `\u20B1${parseFloat(user.salary).toLocaleString("en-PH", {
                    minimumFractionDigits: 2,
                  })}`
                : "N/A"}
            </Text>
          </View>
        ))}
      </View>
    </Page>
  </Document>
);

// ✅ Export correctly
export default SalaryPDF;
