import React from "react";

function ConfirmBox({ open, closeDialog, title, deleteFunction }) {
  if (!open) return null; // ✅ Ensures the modal only renders when open

  return (
    <div className="modal is-active">
      <div className="modal-background" onClick={closeDialog}></div>

      <div className="modal-card">
        {/* Header */}
        <header className="modal-card-head">
          <p className="modal-card-title">Delete {title || "this item"}</p>
          <button className="delete" aria-label="close" onClick={closeDialog}></button>
        </header>

        {/* Body */}
        <section className="modal-card-body">
          <p>Are you sure you want to delete {title || "this item"}?</p>
        </section>

        {/* Footer */}
        <footer className="modal-card-foot is-flex is-justify-content-flex-end">
          <button className="button is-light" onClick={closeDialog}>Cancel</button>
          <button className="button is-danger" onClick={deleteFunction}>Delete</button>
        </footer>
      </div>
    </div>
  );
}

export default ConfirmBox;
