import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { pdf } from "@react-pdf/renderer";
import EmployePDF from "../Printing/EmployeePDF"; // ✅ Import the PDF component
import ConfirmBox from "../Modal/ConfirmBox";

const UserList = () => {
  const [users, setUser] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [entriesToShow, setEntriesToShow] = useState(10);
  const [deleteData, setDeleteData] = useState(null);
  const [open, setOpen] = useState(false);

  useEffect(() => {
    getUsers();
  }, []);

  const getUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/users");
      setUser(response.data);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  function openDelete(user) {
    setDeleteData(user); // ✅ Pass the full user object
    setOpen(true);
  }

  const deleteUser = async () => {
    if (!deleteData) return;
    try {
      await axios.delete(`http://localhost:5000/users/${deleteData.id}`);
      getUsers();
      setOpen(false);
    } catch (error) {
      console.error("Error deleting user:", error);
    }
  };

  const filteredUsers = users.filter((user) =>
    `${user.full_name} ${user.email} ${user.Job_Title} ${user.position} ${user.id}`
      .toLowerCase()
      .includes(searchTerm.toLowerCase())
  );

  const displayedUsers = filteredUsers.slice(0, entriesToShow);

  const handleViewPDF = async () => {
    const blob = await pdf(<EmployePDF users={users} />).toBlob();
    const url = URL.createObjectURL(blob);
    window.open(url);
  };

  return (
    <div className="container mt-5">
      <div className="is-flex is-justify-content-space-between mb-3">
        <input
          type="text"
          className="input"
          style={{ maxWidth: "300px" }}
          placeholder="Search by Employee ID, Name, Email, Job Title, or Position..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <div>
          <Link to="AddEmployee" className="button is-success mr-2">
            Add Employee
          </Link>
          <button onClick={handleViewPDF} className="button is-info">
            View PDF
          </button>
        </div>
      </div>

      <table className="table is-striped is-fullwidth">
        <thead>
          <tr>
            <th>Employee ID</th>
            <th>Full Name</th>
            <th>Email</th>
            <th>Gender</th>
            <th>Job Title</th>
            <th>Position / Level</th>
            <th>Salary</th>
            <th>Annual Salary</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {displayedUsers.length > 0 ? (
            displayedUsers.map((user) => (
              <tr key={user.id}>
                <td>{user.id}</td>
                <td>{user.full_name}</td>
                <td>{user.email}</td>
                <td>{user.gender}</td>
                <td>{user.Job_Title}</td>
                <td>{user.position}</td>
                <td>{user.salary ? `₱${parseFloat(user.salary).toLocaleString("en-PH", { minimumFractionDigits: 2 })}` : ""}</td>
                <td>{user.anual_salary ? `₱${parseFloat(user.anual_salary).toLocaleString("en-PH", { minimumFractionDigits: 2 })}` : ""}</td>
                <td>
                  <Link to={`edit/${user.id}`} className="button is-small is-info mr-2">
                    Edit
                  </Link>
                  <button onClick={() => openDelete(user)} className="button is-small is-danger">
                    Delete
                  </button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="9" className="has-text-centered">
                No users found.
              </td>
            </tr>
          )}
        </tbody>
      </table>

      <div className="is-flex is-align-items-center is-justify-content-space-between mt-3">
        <div>
          <label className="mr-2">Show entries:</label>
          <div className="select">
            <select value={entriesToShow} onChange={(e) => setEntriesToShow(Number(e.target.value))}>
              <option value="5">5</option>
              <option value="10">10</option>
              <option value="20">20</option>
              <option value={filteredUsers.length}>All</option>
            </select>
          </div>
        </div>
        <p>Showing {displayedUsers.length} of {filteredUsers.length} entries</p>
      </div>

      <ConfirmBox
        open={open}
        closeDialog={() => setOpen(false)}
        title={deleteData?.full_name}
        deleteFunction={deleteUser}
      />
    </div>
  );
};

export default UserList;
