import {
  Button,
  Dialog,
  DialogContent,
  Fade,
  Grid,
  IconButton,
  Typography,
} from "@mui/material";
import { Box } from "@mui/system";
import React, { forwardRef } from "react";
import CloseIcon from "@mui/icons-material/Close"; // ✅ MUI Close Icon

const Transition = forwardRef(function Transition(props, ref) {
  return <Fade ref={ref} {...props} />;
});

function ConfirmBox({ open, closeDialog, title, deleteFunction }) {
  return (
    <Dialog
      fullWidth
      open={open}
      maxWidth="md"
      scroll="body"
      onClose={closeDialog} // ✅ Fixes `onBackdropClick`
      TransitionComponent={Transition}
    >
      <DialogContent sx={{ px: 8, py: 6, position: "relative" }}>
        {/* ✅ Uses MUI Close Icon for better UI */}
        <IconButton
          size="medium"
          onClick={closeDialog}
          sx={{ position: "absolute", right: "1rem", top: "1rem" }}
        >
          <CloseIcon />
        </IconButton>

        <Grid container spacing={6}>
          <Grid item xs={12}>
            <Box
              sx={{
                mb: 3,
                display: "flex",
                justifyContent: "flex-start",
                flexDirection: "column",
              }}
            >
              <Typography variant="h5">Delete {title || "this item"}</Typography>
              <Typography variant="body1">
                Are you sure you want to delete {title || "this item"}?
              </Typography>
            </Box>
          </Grid>

          {/* ✅ Button Actions */}
          <Grid
            item
            xs={12}
            sx={{ display: "flex", justifyContent: "flex-end", gap: "1rem" }}
          >
            <Button onClick={closeDialog} size="medium" variant="contained" color="primary">
              Cancel
            </Button>
            <Button onClick={deleteFunction} size="medium" variant="contained" color="error">
              Delete
            </Button>
          </Grid>
        </Grid>
      </DialogContent>
    </Dialog>
  );
}

export default ConfirmBox;
