import React from "react";
import {
  Page,
  Text,
  View,
  Document,
  StyleSheet,
  Font,
} from "@react-pdf/renderer";

// Register a font that supports the Peso sign (₱)
Font.register({
  family: "Roboto",
  src: "https://fonts.gstatic.com/s/roboto/v27/KFOmCnqEu92Fr1Mu72xKOzY.woff2",
});

// Define styles
const styles = StyleSheet.create({
  page: { padding: 20, fontSize: 10, fontFamily: "Roboto" }, // Use Roboto
  header: {
    fontSize: 18,
    textAlign: "center",
    marginBottom: 15,
    fontWeight: "bold",
    color: "#333",
  },
  table: {
    display: "table",
    width: "100%",
    borderStyle: "solid",
    borderWidth: 1,
    borderColor: "#000",
    marginBottom: 10,
  },
  tableRow: { flexDirection: "row", backgroundColor: "#f9f9f9" },
  tableHeader: {
    flexDirection: "row",
    backgroundColor: "#4CAF50",
    color: "#fff",
    fontWeight: "bold",
  },
  tableCell: {
    borderStyle: "solid",
    borderWidth: 1,
    padding: 8,
    fontSize: 10,
    textAlign: "center",
    flex: 1,
    wordBreak: "break-all", // Ensure long words wrap
  },
  emailCell: {
    flex: 2, // Make email column wider
    wordBreak: "break-all",
    overflow: "hidden",
  },
  boldText: { fontWeight: "bold" },
});

// Employee PDF Component
const EmployeePDF = ({ users }) => (
  <Document>
    <Page size="A4" style={styles.page}>
      <Text style={styles.header}>Employee Report</Text>
      <View style={styles.table}>
        {/* Table Header */}
        <View style={styles.tableHeader}>
          <Text style={[styles.tableCell, styles.boldText]}>Employee ID</Text>
          <Text style={[styles.tableCell, styles.boldText]}>Full Name</Text>
          <Text style={[styles.tableCell, styles.boldText, styles.emailCell]}>
            Email
          </Text>
          <Text style={[styles.tableCell, styles.boldText]}>Job Title</Text>
          <Text style={[styles.tableCell, styles.boldText]}>Salary</Text>
        </View>
        {/* Table Body */}
        {users.map((user, index) => (
          <View
            style={[
              styles.tableRow,
              { backgroundColor: index % 2 === 0 ? "#fff" : "#f1f1f1" },
            ]}
            key={user.id}
          >
            <Text style={styles.tableCell}>{user.id}</Text>
            <Text style={styles.tableCell}>{user.full_name}</Text>
            <Text style={[styles.tableCell, styles.emailCell]}>
              {user.email}
            </Text>
            <Text style={styles.tableCell}>{user.Job_Title}</Text>
            <Text style={styles.tableCell}>
              {user.salary
                ? `₱${parseFloat(user.salary).toLocaleString("en-PH", {
                    minimumFractionDigits: 2,
                  })}`
                : "N/A"}
            </Text>
          </View>
        ))}
      </View>
    </Page>
  </Document>
);

// Export component
export default EmployeePDF;
