import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { PDFDownloadLink, pdf } from "@react-pdf/renderer";
import EmployeePDF from "../Printing/EmployeePDF"; // ‚úÖ Fixed import name

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [entriesToShow, setEntriesToShow] = useState(10);

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/users");
      setUsers(response.data);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  const deleteUser = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/users/${id}`);
      fetchUsers();
    } catch (error) {
      console.error("Error deleting user:", error);
    }
  };

  // üîç Search Filter Logic
  const filteredUsers = users.filter((user) =>
    `${user.full_name} ${user.email} ${user.Job_Title} ${user.position} ${user.id}`
      .toLowerCase()
      .includes(searchTerm.toLowerCase())
  );

  // üî¢ Limit Displayed Entries
  const displayedUsers = filteredUsers.slice(0, entriesToShow);

  // üìÑ Download PDF Function
  const downloadPDF = async () => {
    const blob = await pdf(<EmployeePDF users={filteredUsers} />).toBlob();
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "Employee_Report.pdf";
    a.click();
    URL.revokeObjectURL(url);
  };

  return (
    <div className="container mt-5">
      {/* üîç Search & Action Buttons */}
      <div className="is-flex is-justify-content-space-between mb-3">
        <input
          type="text"
          className="input"
          style={{ maxWidth: "300px" }}
          placeholder="Search by Employee ID, Name, Email, Job Title, or Position..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          aria-label="Search employees"
        />
        <div>
          <Link to="AddEmployee" className="button is-success mr-2">
            Add Employee
          </Link>

          <PDFDownloadLink
            document={<EmployeePDF users={filteredUsers} />}
            fileName="Employee_Report.pdf"
            className="button is-info mr-3"
          >
            {({ loading }) => (loading ? "Generating PDF..." : "Print PDF")}
          </PDFDownloadLink>
        </div>
      </div>

      {/* üìÑ Employee Table */}
      <table className="table is-striped is-fullwidth">
        <thead>
          <tr>
            <th>Employee ID</th>
            <th>Full Name</th>
            <th>Email</th>
            <th>Gender</th>
            <th>Job Title</th>
            <th>Position / Level</th>
            <th>Salary</th>
            <th>Annual Salary</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {displayedUsers.length > 0 ? (
            displayedUsers.map((user) => (
              <tr key={user.id}>
                <td>{user.id}</td>
                <td>{user.full_name}</td>
                <td>{user.email}</td>
                <td>{user.gender}</td>
                <td>{user.Job_Title}</td>
                <td>{user.position}</td>
                <td>
                  {user.salary
                    ? parseFloat(user.salary).toLocaleString("en-PH", {
                        minimumFractionDigits: 2,
                      })
                    : "N/A"}
                </td>
                <td>
                  {user.annual_salary // üîπ Fixed incorrect prop name
                    ? parseFloat(user.annual_salary).toLocaleString("en-PH", {
                        minimumFractionDigits: 2,
                      })
                    : "N/A"}
                </td>
                <td>
                  <Link
                    to={`edit/${user.id}`}
                    className="button is-small is-info mr-2"
                  >
                    Edit
                  </Link>
                  <button
                    onClick={() => deleteUser(user.id)}
                    className="button is-small is-danger"
                    aria-label={`Delete ${user.full_name}`}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="9" className="has-text-centered">
                No users found.
              </td>
            </tr>
          )}
        </tbody>
      </table>

      {/* üìä Entries Filter & Display Count */}
      <div className="is-flex is-align-items-center is-justify-content-space-between mt-3">
        <div>
          <label className="mr-2">Show entries:</label>
          <div className="select">
            <select
              value={entriesToShow}
              onChange={(e) => setEntriesToShow(Number(e.target.value))}
            >
              <option value="5">5</option>
              <option value="10">10</option>
              <option value="20">20</option>
              <option value={filteredUsers.length}>All</option>
            </select>
          </div>
        </div>
        <p>
          Showing {displayedUsers.length} of {filteredUsers.length} entries
        </p>
      </div>
    </div>
  );
};

export default UserList;
