import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { pdf } from "@react-pdf/renderer";
import EmployePDF from "../Printing/EmployeePDF";
import ConfirmBox from "../Modal/ConfirmBox";

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [entriesToShow, setEntriesToShow] = useState(10);
  const [deleteData, setDeleteData] = useState(null);
  const [open, setOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);

  useEffect(() => {
    getUsers();
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const getUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/users");
      setUsers(response.data);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  const filteredUsers = users.filter((user) =>
    `${user.full_name} ${user.email} ${user.Job_Title} ${user.position} ${user.id}`
      .toLowerCase()
      .includes(searchTerm.toLowerCase())
  );

  const displayedUsers = filteredUsers.slice(0, entriesToShow);

  const openDelete = (user) => {
    setDeleteData(user);
    setOpen(true);
  };

  const deleteUser = async () => {
    if (!deleteData) return;
    try {
      await axios.delete(`http://localhost:5000/users/${deleteData.id}`);
      getUsers();
      setOpen(false);
    } catch (error) {
      console.error("Error deleting user:", error);
    }
  };

  const handleViewPDF = async () => {
    const blob = await pdf(<EmployePDF users={users} />).toBlob();
    const url = URL.createObjectURL(blob);
    window.open(url);
  };

  return (
    <div className="container mt-5">
      {/* Search and Actions */}
      <div className="columns is-vcentered mb-3">
        <div className="column is-half">
          <input
            type="text"
            className="input"
            placeholder="Search Job Title..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
        <div className="column has-text-right">
          <Link to="/AddJob" className="button is-success mr-2">
            Add Job Title
          </Link>
          <button onClick={handleViewPDF} className="button is-info">
            View PDF
          </button>
        </div>
      </div>

      {/* Entries Control */}
      <div className="mb-3">
        <label className="mr-2">Show entries:</label>
        <div className="select">
          <select value={entriesToShow} onChange={(e) => setEntriesToShow(Number(e.target.value))}>
            <option value="5">5</option>
            <option value="10">10</option>
            <option value="20">20</option>
            <option value={filteredUsers.length}>All</option>
          </select>
        </div>
      </div>

      {/* Table or Mobile List */}
      {!isMobile ? (
        <div className="table-container">
          <table className="table is-striped is-hoverable is-fullwidth">
            <thead>
              <tr>
                <th>ID</th>
                <th>Full Name</th>
                <th>Email</th>
                <th>Gender</th>
                <th>Job Title</th>
                <th>Position</th>
                <th>Salary</th>
                <th>Annual Salary</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {displayedUsers.length > 0 ? (
                displayedUsers.map((user) => (
                  <tr key={user.id}>
                    <td>{user.id}</td>
                    <td>{user.full_name}</td>
                    <td>{user.email}</td>
                    <td>{user.gender}</td>
                    <td>{user.Job_Title}</td>
                    <td>{user.position}</td>
                    <td>{
                      user.salary
                        ? `₱${parseFloat(user.salary).toLocaleString("en-PH", { minimumFractionDigits: 2 })}`
                        : "-"
                    }</td>
                    <td>{
                      user.anual_salary
                        ? `₱${parseFloat(user.anual_salary).toLocaleString("en-PH", { minimumFractionDigits: 2 })}`
                        : "-"
                    }</td>
                    <td>
                      <div className="buttons">
                        <Link to={`edit/${user.id}`} className="button is-small is-info">
                          Edit
                        </Link>
                        <button onClick={() => openDelete(user)} className="button is-small is-danger">
                          Delete
                        </button>
                      </div>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="9" className="has-text-centered">No users found.</td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      ) : (
        <div className="columns is-multiline">
          {displayedUsers.map((user) => (
            <div key={user.id} className="column is-12">
              <div className="box">
                <p><strong>ID:</strong> {user.id}</p>
                <p><strong>Name:</strong> {user.full_name}</p>
                <p><strong>Email:</strong> {user.email}</p>
                <p><strong>Job Title:</strong> {user.Job_Title}</p>
                <p><strong>Position:</strong> {user.position}</p>
                <div className="buttons mt-2">
                  <Link to={`edit/${user.id}`} className="button is-small is-info">Edit</Link>
                  <button onClick={() => openDelete(user)} className="button is-small is-danger">Delete</button>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Delete Confirmation */}
      <ConfirmBox
        open={open}
        closeDialog={() => setOpen(false)}
        title={deleteData?.full_name}
        deleteFunction={deleteUser}
      />
    </div>
  );
};

