import { StyleSheet, Font } from "@react-pdf/renderer";

// Register a modern font (Poppins) using a TTF file
Font.register({
  family: "Poppins",
  src: "https://raw.githubusercontent.com/google/fonts/main/ofl/poppins/Poppins-Regular.ttf",
});


const styles = StyleSheet.create({
  page: { padding: 25, fontSize: 11, fontFamily: "Poppins", backgroundColor: "#f9f9f9" },

  header: {
    fontSize: 20,
    textAlign: "center",
    marginBottom: 20,
    fontWeight: "bold",
    textTransform: "uppercase",
    color: "#2C3E50", // Dark grayish blue for a modern look
  },

  table: {
    display: "table",
    width: "100%",
    borderRadius: 5, // Rounded table
    overflow: "hidden",
  },

  tableHeader: {
    flexDirection: "row",
    backgroundColor: "#2C3E50", // Dark modern blue
    color: "#FFFFFF",
    fontWeight: "bold",
    padding: 8,
    borderRadius: 5,
  },

  tableRow: {
    flexDirection: "row",
    borderBottomWidth: 1,
    borderColor: "#ddd",
    backgroundColor: "#fff", // White background for rows
  },

  tableRowAlternate: {
    backgroundColor: "#f2f2f2", // Light gray for alternating rows
  },

  tableCell: {
    flex: 1,
    borderRightWidth: 1,
    borderColor: "#ddd",
    padding: 10,
    fontSize: 11,
    textAlign: "center",
    color: "#34495E", // Modern dark gray text
  },

  emailCell: {
    flex: 2, // Make email column wider
    wordBreak: "break-word",
    color: "#2980B9", // Blue for email for a clean look
  },

  salaryCell: {
    flex: 1,
    padding: 10,
    fontSize: 11,
    textAlign: "right", // Right-align salary
    fontWeight: "bold",
    color: "#27AE60", // Green to highlight salary
  },
});

export default styles;
